function ngjsAddEvent(id) {
    var obj = jQuery("#"+cont_id+"_ngjspins_" + id);
    if (pins_config.pins[id].active === !0) {
        obj.attr({
            cursor: "pointer"
        });
        obj.hover(function() {
            jQuery("#"+cont_id+"_jstip").show().html(pins_config.pins[id].hover);
            obj.css({
                fill: pins_config.pins[id].overColor
            });
        }, function() {
            jQuery("#"+cont_id+"_jstip").hide();
            obj.css({
                fill: pins_config.pins[id].upColor
            });
        });
        obj.mouseup(function() {
            obj.css({
                fill: pins_config.pins[id].overColor
            });
            if (pins_config.pins[id].target === "new_window") {
                window.open(pins_config.pins[id].url);
            } else if (pins_config.pins[id].target === "same_window") {
                window.parent.location.href = pins_config.pins[id].url;
            } else if (pins_config.pins[id].target === "modal") {
                jQuery(pins_config.pins[id].url).modal("show");
            }
        });
        obj.mousemove(function(e) {
            var x = e.pageX + 10, y = e.pageY + 15;
            var tipw = jQuery("#"+cont_id+"_jstip").outerWidth(), tiph = jQuery("#"+cont_id+"_jstip").outerHeight(), x = x + tipw > jQuery(document).scrollLeft() + jQuery(window).width() ? x - tipw - 20 * 2 : x;
            y = y + tiph > jQuery(document).scrollTop() + jQuery(window).height() ? jQuery(document).scrollTop() + jQuery(window).height() - tiph - 10 : y;
            jQuery("#"+cont_id+"_jstip").css({
                left: x,
                top: y
            });
        });
        if (isTouchEnabled()) {

            obj.on("touchstart", function(e) {
                var touch = e.originalEvent.touches[0];
                var x = touch.pageX + 10, y = touch.pageY + 15;
                var tipw = jQuery("#"+cont_id+"_jstip").outerWidth(), tiph = jQuery("#"+cont_id+"_jstip").outerHeight(), x = x + tipw > jQuery(document).scrollLeft() + jQuery(window).width() ? x - tipw - 20 * 2 : x;
                y = y + tiph > jQuery(document).scrollTop() + jQuery(window).height() ? jQuery(document).scrollTop() + jQuery(window).height() - tiph - 10 : y;
                jQuery("#"+cont_id+"_jstip").show().html(pins_config.pins[id].hover);
                jQuery("#"+cont_id+"_jstip").css({
                    left: x,
                    top: y
                });
            });
            obj.on("touchend", function() {
                jQuery("#" + id).css({
                    fill: pins_config.pins[id].upColor
                });
                if (pins_config.pins[id].target === "new_window") {
                    window.open(pins_config.pins[id].url);
                } else if (pins_config.pins[id].target === "same_window") {
                    window.parent.location.href = pins_config.pins[id].url;
                } else if (pins_config.pins[id].target === "modal") {
                    jQuery(pins_config.pins[id].url).modal("show");
                }
            });
        }
    }
}
